const ENDPOINT = 'https://api.mapbox.com/directions/v5/mapbox';

// Map the coordinates to be an array of arrays, example: [[0, 0], [180, 0]]
function mapCoordinate(x) {
  // Check if geopoint
  if (typeof x === 'object' && x.latitude && x.longitude) {
    return [x.latitude, x.longitude].join(",");
  }
  // Check if just plain array
  if (Array.isArray(x) && x.length === 2) {
    return x.join(",");
  }
  throw new Error(`Unexpected Coordinate type. (${JSON.stringify(x)})`);
}

// Define an asynchronous function to make the API request
async function makeRequest() {
  // Get the Mapbox access token from Noodl project settings
  const access_token = Noodl.getProjectSettings().mapboxAccessToken;
  
  const routingProfile = Script.Inputs.Profile || "driving-traffic";

  // Encode the coordinates to be URL-safe
  const coordinates = encodeURIComponent(Script.Inputs.Coordinates.map(mapCoordinate).join(";"));

  // Define query parameters for the API request
  // 
  // Playground by Mapbox to test out all the features:
  // https://docs.mapbox.com/playground/directions/
  const queryParams = {
    access_token, // Provide the access token
    alternatives: true,
    continue_straight: true,
    geometries: "geojson",
    language: "en",
    overview: "simplified",
    steps: true
  };

  // Construct the query string from the query parameters
  const query = Object.keys(queryParams)
    .filter((key) => !!queryParams[key]) // Filter out empty values
    .map((key) => `${encodeURIComponent(key)}=${encodeURIComponent(queryParams[key])}`)
    .join('&');

  // Make the API request and get the response as JSON
  const response = await fetch(`${ENDPOINT}/${routingProfile}/${coordinates}?${query}`);
  const json = await response.json();

  if (json.code !== "Ok") {
    console.debug(json);
    throw new Error(`Mapbox Directions API, invalid response. (${json.code})`);
  }

  Script.Outputs.Routes = json.routes;
  Script.Outputs.Done();
}

Script.Signals = {
  Run() {
    makeRequest();
  },
};

Script.Inputs = {
  MapboxObject: "object",
  Routes: "array"
};

Script.Outputs = {
  Done: "signal",
};


Script.Signals = {
  Update() {
    const map = Script.Inputs.MapboxObject;

    const route = Script.Inputs.Routes[0];

    function createOrUpdateSource(id, newSource) {
      const source = map.getSource(id);
      if (source) {
        source.setData(newSource.data);
      } else {
        map.addSource(id, newSource);
      }
    }

    function createOrUpdateLayer(newLayer) {
      const layer = map.getLayer(newLayer.id);
      if (layer) {
        if (newLayer.paint) {
          Object.keys(newLayer.paint).forEach((key) => {
            layer.setPaintProperty(key, newLayer.paint[key]);
          });
        }
      } else {
        map.addLayer(newLayer);
      }
    }

    createOrUpdateSource('route', {
      'type': 'geojson',
      'data': {
        'type': 'Feature',
        'properties': {},
        'geometry': route.geometry,
      }
    });

    createOrUpdateLayer({
      'id': 'route',
      'type': 'line',
      'source': 'route',
      'layout': {
        'line-join': 'round',
        'line-cap': 'round'
      },
      'paint': {
        'line-color': '#888',
        'line-width': 8
      }
    });
  },
};

Node.Inputs = {
    Map: "*"
}

Node.Outputs = {
    ScreenX: "number",
    ScreenY: "number",
    Lon: "number",
    Lat: "number",
}

Node.Signals.Setup = function () {
    const map = Node.Inputs.Map;

    map.on('mousemove', (e) => {
        Script.setOutputs({
            // `e.point` is the x, y coordinates of the `mousemove` event
            // relative to the top-left corner of the map.
            ScreenX: e.point.x,
            ScreenY: e.point.y,
            // `e.lngLat` is the longitude, latitude geographical
            // position of the event.
            Lon: e.lngLat.lng,
            Lat: e.lngLat.lat
        });
    });
}

