/**
 * This function takes a number representing an area in
 * square meters and returns a string with the area
 * formatted nicely, using different units of measurement
 * depending on the magnitude of the area value.
 * 
 * For small areas, it uses square meters;
 * For medium areas, it uses hectares;
 * And for large areas, it uses square kilometers.
 * 
 * The toLocaleString() method formats the number with commas and
 * the correct decimal separator for the user's locale,
 * while the toFixed() method rounds the result to 2 decimal places.
 */
function formatAreaWithUnit(area) {
  const magnitude = Math.floor(Math.log10(area));
  if (magnitude < 3) {
    // For small areas, just use square meters
    return area.toLocaleString('en-US', { maximumFractionDigits: 2 }) + ' m²';
  } else if (magnitude < 6) {
    // For medium areas, use hectares (1 ha = 10,000 m²)
    const areaInHa = (area / 10000).toFixed(2);
    return areaInHa + ' ha';
  } else {
    // For large areas, use square kilometers (1 km² = 1,000,000 m²)
    const areaInKmSq = (area / 1000000).toFixed(2);
    return areaInKmSq + ' km²';
  }
}

