let renderer;
let scene;
let camera;
let cameraTransform;
let resizeObserver;
let previousWidth, previousHeight;

// https://jsfiddle.net/5vpL7ays/7/

function getCenterCoordinates() {
  return {
    lat: Script.Inputs.Lat,
    lng: Script.Inputs.Lng,
  }
}

class NoodlRaycaster {
  constructor(map) {
    this.map = map;

    this.raycaster = new THREE.Raycaster();
    this.raycaster.near = -1;
    this.raycaster.far = 1e6;
  }

  raycast(point) {
    var mouse = new THREE.Vector2();
    // // scale mouse pixel position to a percentage of the screen's width and height
    mouse.x = (point.x / this.map.transform.width) * 2 - 1;
    mouse.y = 1 - (point.y / this.map.transform.height) * 2;

    const camInverseProjection = new THREE.Matrix4().copy(camera.projectionMatrix).invert();
    const cameraPosition = new THREE.Vector3().applyMatrix4(camInverseProjection);
    const mousePosition = new THREE.Vector3(mouse.x, mouse.y, 1).applyMatrix4(camInverseProjection);
    const viewDirection = mousePosition.clone().sub(cameraPosition).normalize();

    this.raycaster.set(cameraPosition, viewDirection);

    // calculate objects intersecting the picking ray
    return this.raycaster.intersectObjects(scene.children, true);
  }
}

function updateProjectionMatrix() {
  const centerLngLat = getCenterCoordinates();
  const center = mapboxgl.MercatorCoordinate.fromLngLat(centerLngLat, 0);
  const s = center.meterInMercatorCoordinateUnits();

  const scale = new THREE.Matrix4().makeScale(s, s, -s);
  const rotation = new THREE.Matrix4().multiplyMatrices(
    new THREE.Matrix4().makeRotationX(-0.5 * Math.PI),
    new THREE.Matrix4().makeRotationY(Math.PI));

  cameraTransform = new THREE.Matrix4()
    .multiplyMatrices(scale, rotation)
    .setPosition(center.x, center.y, center.z);

  // Output the scene origin
  Script.Outputs.Origin = center;
  Script.Outputs.UnitScale = Math.cos(centerLngLat.lat * (Math.PI / 180));
}

Script.OnDestroy = function () {
  if (resizeObserver) {
    resizeObserver.disconnect();
  }
}

function createRenderer(canvas, gl) {
  const width = Number(canvas.getAttribute("width"));
  const height = Number(canvas.getAttribute("height"));

  if (renderer) {
    renderer.dispose();
    renderer = null;
  }

  // ---
  // Create the Three.js Renderer
  renderer = new THREE.WebGLRenderer({
    canvas: canvas,
    context: gl,
    antialias: true,
  });
  renderer.autoClear = false;

  // ---
  // Add ShadowMap
  renderer.shadowMap.enabled = true;
  renderer.shadowMap.type = THREE.PCFSoftShadowMap;

  // ---
  // Create Camera
  camera = new THREE.PerspectiveCamera(45, width / height, 0.1, 1e6);
  updateProjectionMatrix();
}

Script.Signals.CreateScene = function () {
  const map = Script.Inputs["Mapbox Object"];
  const canvas = map.getCanvas();

  map.addLayer({
    id: `three.js-layer`,
    type: 'custom',
    renderingMode: '3d',
    onAdd(map, gl) {
      createRenderer(canvas, gl);

      const width = Number(canvas.getAttribute("width"));
      const height = Number(canvas.getAttribute("height"));
      previousWidth = width;
      previousHeight = height;

      // ---
      // Create the Three.js Scene
      scene = new THREE.Scene();
      scene.add(new THREE.AmbientLight(0xffffff, 0.5));

      const light = new THREE.DirectionalLight(0xffffff, 0.5);
      light.position.set(5, 10, 5); // default; light shining from top
      light.castShadow = true; // default false
      scene.add(light);

      light.shadow.mapSize.width = 512 * 2;
      light.shadow.mapSize.height = 512 * 2;
      light.shadow.camera.near = 0.5;
      light.shadow.camera.far = 500;

      // Default is 5, this changes the shadow map size.
      // Changing this also changes the quality since the map texture size is 512x512
      const shadowCameraSize = 20;
      light.shadow.camera.top = shadowCameraSize;
      light.shadow.camera.right = shadowCameraSize;
      light.shadow.camera.bottom = -shadowCameraSize;
      light.shadow.camera.left = -shadowCameraSize;

      // Create a helper for the shadow camera (optional)
      // const helper = new THREE.CameraHelper(light.shadow.camera);
      // scene.add(helper);

      // ---
      // Add Group
      const planeGeometry = new THREE.PlaneGeometry(50, 50);
      const planeMaterial = new THREE.MeshStandardMaterial({
        transparent: true,
        color: 0x000000,
        blending: THREE.AdditiveBlending
      });
      const plane = new THREE.Mesh(planeGeometry, planeMaterial);
      plane.receiveShadow = true;
      plane.userData = { kind: 'ground' };
      plane.rotation.x = -Math.PI / 2; // Rotate the plane 90 degrees around the x-axis
      scene.add(plane);

      // ---
      // Create the Raycaster
      Script.Outputs.Raycaster = new NoodlRaycaster(map);

      // ---
      // Resizer
      resizeObserver = new ResizeObserver(() => {
        setTimeout(() => {
          const width = Number(canvas.getAttribute("width"));
          const height = Number(canvas.getAttribute("height"));

          if (previousWidth !== width || previousHeight !== height) {
            previousWidth = width;
            previousHeight = height;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();

            updateProjectionMatrix();

            createRenderer(canvas, gl);

            map.triggerRepaint();
          }
        }, 0);
      });

      const mapContainer = map.getContainer();
      resizeObserver.observe(mapContainer);

      // --- Three.js Scene is created!
      Script.Outputs.MapboxObject = map;
      Script.Outputs.Scene = scene;
      Script.Outputs.Created();
    },
    render(_gl, matrix) {
      camera.projectionMatrix = new THREE.Matrix4()
        .fromArray(matrix)
        .multiply(cameraTransform);

      renderer.resetState();
      renderer.render(scene, camera);

      map.triggerRepaint();
    }
  });
}

let objects = {};
let activeId;
let moveObjectState = null;

let isSpacebarDown = false;

// Add a keydown event listener to the window
window.addEventListener('keydown', function (event) {
  if (event.key === ' ' || event.key === 'Spacebar') {
    isSpacebarDown = true;
  }
});

// Add a keyup event listener to the window
window.addEventListener('keyup', function (event) {
  if (event.key === ' ' || event.key === 'Spacebar') {
    isSpacebarDown = false;
  }
});

function degreesToRadians(degrees) {
  return degrees * (Math.PI / 180);
}
function radiansToDegrees(radians) {
  return radians * (180 / Math.PI);
}

const IsHouse = (x) => x.object.parent.userData.kind === 'house';
const IsGround = (x) => x.object.userData.kind === 'ground';

// Disable/Enable all Mapbox inputs
function toggleMapInput(value) {
  const map = Script.Inputs.mapObject
  if (value) {
    map.scrollZoom.enable();
    map.boxZoom.enable();
    map.dragRotate.enable();
    map.dragPan.enable();
    map.keyboard.enable();
    map.doubleClickZoom.enable();
    map.touchZoomRotate.enable();
  } else {
    map.scrollZoom.disable();
    map.boxZoom.disable();
    map.dragRotate.disable();
    map.dragPan.disable();
    map.keyboard.disable();
    map.doubleClickZoom.disable();
    map.touchZoomRotate.disable();
  }
}

function onMouseMove(e) {
  const raycaster = Script.Inputs.raycaster
  const hitResults = raycaster.raycast(e.point);

  if (moveObjectState) {
    const current = objects[moveObjectState.id];
    const ground = hitResults.filter(IsGround)[0];
    if (ground) {
      const objId = current.userData.id
      const unitScale = Script.Inputs.UnitScale

      if (isSpacebarDown) {
        // Update the house rotation
        current.rotation.y = current.rotation.y - 0.01 * e.originalEvent.movementY;

        Noodl.Objects[objId].rotation = radiansToDegrees(current.rotation.y)
      } else {
        // Update the house position
        const newPosition = ground.point.sub(moveObjectState.offset);
        current.position.set(newPosition.x, 0, newPosition.z);

        Noodl.Objects[objId].x = newPosition.x / unitScale
        Noodl.Objects[objId].y = newPosition.z / unitScale
      }

      Script.Outputs.Updated();
    }
    return;
  }

  if (hitResults.length && hitResults[0].object.parent.userData.kind === 'house') {
    const hit = hitResults[0];
    Noodl.Objects["Interact"].HoverId = hit.object.parent.userData.id
  } else {
    Noodl.Objects["Interact"].HoverId = ""
  }
}

function onClick(e) {
  const raycaster = Script.Inputs.raycaster
  const hitResults = raycaster.raycast(e.point);

  if (hitResults.length && hitResults[0].object.parent.userData.kind === 'house') {
    const hit = hitResults[0];
    Noodl.Objects["Interact"].ClickId = activeId = hit.object.parent.userData.id

    toggleMapInput(false)

  } else {
    Noodl.Objects["Interact"].ClickId = activeId = ""

    toggleMapInput(true)
  }
}

function onMouseDown(e) {
  const raycaster = Script.Inputs.raycaster
  const hitResults = raycaster.raycast(e.point);
  const firstHouseMesh = hitResults.filter(IsHouse)[0];
  if (hitResults.length && firstHouseMesh) {
    const ground = hitResults.filter(IsGround)[0];
    const houseGroup = firstHouseMesh.object.parent;

    toggleMapInput(false)

    const offset = ground.point.sub(houseGroup.position);

    moveObjectState = {
      id: houseGroup.userData.id,
      offset,
    }
  }
}

function onMouseUp(e) {
  toggleMapInput(true)

  if (moveObjectState) {
    // console.log(moveObjectState)
    moveObjectState = null;
  }
}

Script.OnDestroy = function () {
  const map = Script.Inputs.mapObject
  map.off('mousemove', onMouseMove);
  map.off('click', onClick);
  map.off('mousedown', onMouseDown);
  map.off('mouseup', onMouseUp);
}

Script.Signals.Setup = function () {
  const map = Script.Inputs.mapObject
  // https://docs.mapbox.com/mapbox-gl-js/api/map/#map-events
  map.on('mousemove', onMouseMove);
  map.on('click', onClick);
  map.on('mousedown', onMouseDown);
  map.on('mouseup', onMouseUp);
}

function createHouseGeometry(width, height, length) {
  const roofJoinOffset = 0.01
  const roofExtendedWidth = 0.1

  var house = new THREE.BoxGeometry(width, height, length);
  var houseRoof1 = new THREE.BoxGeometry(width / 2 + roofExtendedWidth, 0.1, length);
  var houseRoof2 = new THREE.BoxGeometry(width / 2 + roofExtendedWidth, 0.1, length);

  // Set the origin to the corner of the house
  house.translate(width / 2, height / 2, length / 2);

  houseRoof1.rotateZ(0.1);
  houseRoof1.translate((width / 4) + roofJoinOffset, height + 0.05, length / 2);
  houseRoof2.rotateZ(-0.1);
  houseRoof2.translate((width / 2 + width / 4) - roofJoinOffset, height + 0.05, length / 2);

  const geometries = [
    house,
    houseRoof1,
    houseRoof2
  ]

  const geometry = THREE.BufferGeometryUtils.mergeBufferGeometries(geometries);
  geometry.computeBoundingSphere();
  return geometry;
}

function createHouse(house) {
  const scene = Script.Inputs["scene"];
  const unitScale = Script.Inputs.UnitScale

  if (objects[house.id]) {
    // Update house
    const houseGroup = objects[house.id];

    const x = house.x * unitScale;
    const z = house.y * unitScale;
    const width = house.width * unitScale;
    const length = house.length * unitScale;
    const height = (house.height || 1) * unitScale;

    const houseMesh = houseGroup.children.find((x) => x.type === "Mesh")
    const geometry = createHouseGeometry(width, height, length);
    houseMesh.geometry = geometry;

    // Add the userdata to this object, and a kind so we can easily scale it later.
    houseGroup.userData = {
      kind: 'house',
      id: house.getId(),
      ...house
    };

    houseGroup.position.x = x;
    houseGroup.position.z = z;
    houseGroup.rotation.y = degreesToRadians(house.rotation);
  } else {
    // Create house
    const x = house.x * unitScale;
    const z = house.y * unitScale;
    const width = house.width * unitScale;
    const length = house.length * unitScale;
    const height = (house.height || 1) * unitScale;

    const geometry = createHouseGeometry(width, height, length);
    const material = new THREE.MeshStandardMaterial({
      color: house.color,
      roughness: 1,
      metalness: 0,
      emissiveIntensity: 0
    });

    const houseMesh = new THREE.Mesh(geometry, material);
    houseMesh.receiveShadow = true;
    houseMesh.castShadow = true;

    const houseGroup = new THREE.Group();
    houseGroup.position.x = x;
    houseGroup.position.z = z;
    houseGroup.rotation.y = degreesToRadians(house.rotation);
    houseGroup.add(houseMesh);
    scene.add(houseGroup);

    // Add the userdata to this object, and a kind so we can easily scale it later.
    houseGroup.userData = {
      kind: 'house',
      id: house.getId(),
      ...house
    };

    objects[house.id] = houseGroup;
  }
}

Script.Signals["Create Or Update All"] = function () {
  if (!Script.Inputs.scene) return;

  Script.Inputs.Items.forEach(createHouse);
}

